import os
from dataclasses import dataclass


class EnvNotFoundError(Exception):
    pass


@dataclass
class TypedEnvVarObject[T]:
    key: str
    optional: bool = False
    credential: bool = False
    default: T | None = None

    @property
    def value(self) -> T | None:
        value = os.getenv(self.key)
        if value is None:
            if self.default or self.optional:
                value = self.default
            else:
                raise EnvNotFoundError(f"Environment variable is not defined! : {self.key}")

        return value

    def __repr__(self):
        try:
            value = self.value if not self.credential else "*****"
        except EnvNotFoundError:
            value = "(not defined)"

        return f"{{{self.key}: {value}}}"


def get_all_envvars_string():
    """Get all environment variables as a string."""
    env_vars = [
        value for key, value in globals().items() if key.startswith("ENV_") and isinstance(value, TypedEnvVarObject)
    ]
    env_vars.sort(key=lambda x: (len(x.key), x.key))
    return str(env_vars)

from __future__ import annotations

from kubernetes.client import ApiClient, Configuration
from kubernetes.config import load_incluster_config, load_kube_config


class K8sClient(ApiClient):
    """A client for Kubernetes API server."""

    @classmethod
    def from_kubeconfig(
        cls,
        kubeconfig_path: str | None = None,
        retries: int = 3,
    ) -> K8sClient:
        """
        Create a K8sClient from a kubeconfig file or in-cluster configuration.
        """
        if kubeconfig_path:
            load_kube_config(config_file=kubeconfig_path)
        else:
            load_incluster_config()

        configuration = Configuration()
        configuration.retries = retries
        return cls(configuration=configuration)
